%[text] # Perform DOA estimation in 3D-ECEF coordinate
%[text] This script performs 3D direction-of-arrival (DOA) estimation in the Earth-Centered Earth-Fixed (ECEF) coordinate system using a 2D uniform rectangular array (URA) mounted on a satellite.
%[text] **Key Features:**
%[text] - Operates fully in the global (ECEF) coordinate system without transforming to a local frame.
%[text] - The array is rotated to point toward the origin to align with the ECEF geometry.
%[text] - The direction of arrival (DOA) is represented as a unit vector in 3D space.
%[text] - MUSIC algorithm is used for 2D DOA estimation (azimuth & elevation) in the local frame.
%[text] - The estimated DOA is converted back to the global frame to estimate user position. \
%[text] **Inputs:**
%[text] - Satellite position (r\_sat) and user position (r\_usr) in meters (ECEF frame)
%[text] - Array geometry (URA), wavelength, SNR, and snapshot count \
%[text] **Outputs:**
%[text] - Estimated direction vector u\_est
%[text] - Estimated user position r\_usr\_est (based on known z\_usr)
%[text] - Estimation error norm(r\_usr\_est - r\_usr) \
%[text] *Note:* The user's z-coordinate is assumed to be known. The full 3D position is then computed by solving a line-plane intersection along the estimated direction.
clear(); close all;
%[text] ## Parameters
wavelen = 1;              % normalized wavelength
elemSpace = wavelen / 2;  % half-wavelength spacing
size = [4 4];             % number of array elements
numSnap = 2000;           % number of signal snapshots
E = wgs84Ellipsoid('meters');
numSource = 1;            % number of signal sources
pwrSource = 1;            % source signal power

% Set position of the user
lat_usr = 35;   % degrees
lon_usr = 120;  % degrees
h_usr   = 0;    % m

[xu, yu, zu] = geodetic2ecef(E, lat_usr, lon_usr, h_usr);
posUsr = [xu; yu; zu];

% Set position of the satellite
h = 550e3;
lat_sat = lat_usr + 10;  % E.g., move 10° in latitude
lon_sat = lon_usr + 10;  % Move 10° in longitude

[x, y, z] = geodetic2ecef(E, lat_sat, lon_sat, h);
posSat = [x; y; z];
%%
%[text] ## Array Formulation
% design a UPA
arrUra = ura_2d(size, elemSpace); 
arrayUpa = createUpa(size, elemSpace); 

% Compute the global unit direction vector from user to satellite (relative to the origin) 
u = (posSat - posUsr) / norm(posSat - posUsr);

% Compute the local doa (relative to the satellite)
u_local = dir2local(-u, -posSat);
doa_local = [atan2(u_local(2), u_local(1)); asin(u_local(3))];
doaGrid = genDoaGrid("angle", 2, [180 45]);
%[text] ## Signal Generation & DOA Estimation
% Signal strength
snr_db = -20:2:10; % signal-to-noise ratio in dB

numParam = length(snr_db);
numRepeat = 2000; % 200 Monte Carlo runs for each parameter

% outcome preparation
mse_mus = zeros(numParam, 1);
mse_mvd = zeros(numParam, 1);
mse_mle = zeros(numParam, 1);

mse_mus_new = zeros(numParam, 1);
mse_mvd_new = zeros(numParam, 1);
mse_mle_new = zeros(numParam, 1);

progressbar('reset', numParam);

for ii = 1:numParam %[output:group:92eb29a9]
  pwrNoise = pwrSource / (10^(snr_db(ii) / 10));

  cur_se_mus = 0;
  cur_se_mvd = 0;
  cur_se_mle = 0;

  cur_se_mus_new = 0;
  cur_se_mvd_new = 0;
  cur_se_mle_new = 0;

  parfor rr = 1:numRepeat
    % Generate signal snapshots with noise
    [~, R, ~] = snapshot_gen_sto(arrUra, doa_local, wavelen, numSnap, pwrNoise, pwrSource);
    sp_mus = music_2d(R, numSource, arrUra, wavelen, [180 45], 'RefineEstimates', true);
    sp_mvd = mvdr_2d(R, numSource, arrUra, wavelen, [180 45], 'RefineEstimates', true);
    sp_mle = mle_sto_uc_2d(R, numSource, arrUra, wavelen);

    [~, R, ~] = snapGenStochastic(arrayUpa, doa_local, wavelen, numSnap, pwrNoise, pwrSource);
    sp_mus_new = estimatorMusic(arrayUpa, wavelen, R, numSource, doaGrid, true);
    sp_mvd_new = estimatorMvdr(arrayUpa, wavelen, R, numSource, doaGrid, true);
    sp_mle_new = estimatorMleStoConOpt(arrayUpa, wavelen, R, numSource, doaGrid);

    cur_se_mus = cur_se_mus + sum((sp_mus.x_est - doa_local).^2);
    cur_se_mvd = cur_se_mvd + sum((sp_mvd.x_est - doa_local).^2);
    cur_se_mle = cur_se_mle + sum((sp_mle.x_est - doa_local).^2);

    cur_se_mus_new = cur_se_mus_new + sum((sp_mus_new.angleEst - doa_local).^2);
    cur_se_mvd_new = cur_se_mvd_new + sum((sp_mvd_new.angleEst - doa_local).^2);
    cur_se_mle_new = cur_se_mle_new + sum((sp_mle_new.angleEst - doa_local).^2);
  end

  mse_mus(ii) = cur_se_mus / (numSource * numRepeat);
  mse_mvd(ii) = cur_se_mvd / (numSource * numRepeat);
  mse_mle(ii) = cur_se_mle / (numSource * numRepeat);

  mse_mus_new(ii) = cur_se_mus_new / (numSource * numRepeat);
  mse_mvd_new(ii) = cur_se_mvd_new / (numSource * numRepeat);
  mse_mle_new(ii) = cur_se_mle_new / (numSource * numRepeat);

  progressbar('advance'); %[output:063dcd2f]
end %[output:group:92eb29a9]
%%
semilogy( ... %[output:9d5bd447]
  snr_db, mse_mle, '-x', ... %[output:9d5bd447]
  snr_db, mse_mvd, '-x', ... %[output:9d5bd447]
  snr_db, mse_mus, '-x', ... %[output:9d5bd447]
  snr_db, mse_mle_new, '-x', ... %[output:9d5bd447]
  snr_db, mse_mvd_new, '-x', ... %[output:9d5bd447]
  snr_db, mse_mus_new, '-x' ... %[output:9d5bd447]
  ); %[output:9d5bd447]
xlabel('SNR (dB)'); ylabel('MSE / rad^2'); grid on; %[output:9d5bd447]
title(['DoA Est with UPA (Single Sat, Sinlge Source, ', num2str(numSnap), ' snaps)']); %[output:9d5bd447]

