%[text] # Perform DOA estimation in 2D-ECEF coordinate
%[text] This model provides a 2D basis for ECEF-based DOA analysis. Instead of using a local array-centered coordinate system, it operates in a global coordinate system centered at the origin. The array is rotated to face the origin, and the direction of arrival is represented as a 2D unit vector in the $xOy$ plane, rather than a single angular value. This allows consistent formulation with global positions and facilitates future extension to 3D-ECEF frameworks.
clear(); close all;
%[text] ## Parameters
% satellite and user positions (in meters)
r_sat = [200e3; 800e3]; % satellite position in XY-plane
r_usr = [0; 300e3]; % user position in XY-plane
numSource = 1; % number of signal sources

u = (r_sat - r_usr) / norm(r_sat - r_usr); % the unit direction vector from user to satellite
theta_sat = atan2(r_sat(2), r_sat(1)); % the satellite's azimuth angle (in radians)
theta_u = atan2(u(2), u(1));
doa_local = theta_sat - theta_u; % the local doa (relative to the satellite)

% Array configurations
wavelen = 1; % normalized wavelength
elemSpace = wavelen / 2; % half-wavelength spacing
elemNum = 12; % number of array elements
numSnap = 2000; % number of signal snapshots

arrayUla = design_array_1d('ula', elemNum, elemSpace); % design a ULA with m elements

% Signal strength
snr_db = -20:10; % signal-to-noise ratio in dB
pwrSource = 1; % source signal power

numParam = length(snr_db);
numRepeat = 2000; % 200 Monte Carlo runs for each parameter
%[text] ## Signal Generation & DOA Estimation
%[text]{"align":"center"} ![2D ECEF.png](text:image:7fbe)
% outcome preparation
mse_mle = zeros(numParam, 1);
mse_mle_grid = zeros(numParam, 1);
mse_rmusic = zeros(numParam, 1);
mse_music = zeros(numParam, 1);
mse_music_refine = zeros(numParam, 1);
crb_sto_uc = zeros(numParam, 1);

crb_sto = zeros(numParam, 1);

progressbar('reset', numParam);

for ii = 1:numParam %[output:group:507a9ea8]
  pwrNoise = pwrSource / (10^(snr_db(ii) / 10));

  cur_se_mle = 0;
  cur_se_mle_grid = 0;
  cur_se_rmusic = 0;
  cur_se_music = 0;
  cur_se_music_refine = 0;

  parfor rr = 1:numRepeat %[output:273a5fae]
    % Generate signal snapshots with noise
    [~, R, S] = snapshot_gen_sto(arrayUla, doa_local, wavelen, numSnap, pwrNoise, pwrSource);

    sp_mle = mle_sto_uc_1d(R, numSource, arrayUla, wavelen);
    % sp_mle_grid = mle_sto_con_grid_1d(R, numSource, arrayUla, wavelen, 180, 'RefineEstimates',true);
    sp_rmusic = rmusic_1d(R, numSource, 2*pi*arrayUla.element_spacing/wavelen);
    sp_music = music_1d(R, numSource, arrayUla, wavelen, 180, 'RefineEstimates',true);

    cur_se_mle = cur_se_mle + sum((sort(sp_mle.x_est, 'ascend') - sort(doa_local, 'ascend')).^2);
    % cur_se_mle_grid = cur_se_mle_grid + sum((sort(sp_mle_grid.x_est, 'ascend') - sort(doa_local, 'ascend')).^2);
    cur_se_rmusic = cur_se_rmusic + sum((sort(sp_rmusic.x_est, 'ascend') - sort(doa_local, 'ascend')).^2);
    cur_se_music = cur_se_music + sum((sort(sp_music.x_est, 'ascend') - sort(doa_local, 'ascend')).^2);
  end

  mse_mle(ii) = cur_se_mle / (numSource * numRepeat);
  % mse_mle_grid(ii) = cur_se_mle_grid / (numSource * numRepeat);
  mse_rmusic(ii) = cur_se_rmusic / (numSource * numRepeat);
  mse_music(ii) = cur_se_music / (numSource * numRepeat);
  crb_sto_uc(ii) = mean(diag(crb_uc_sto_1d(arrayUla, wavelen, doa_local, pwrSource, pwrNoise, numSnap)));

  progressbar('advance'); %[output:45fa8312]
end %[output:group:507a9ea8]
%%
semilogy(snr_db, mse_mle, '-o', snr_db, mse_music, '-x', ... %[output:48b865ed]
    snr_db, mse_rmusic, '-x', snr_db, crb_sto_uc, '--'); %[output:48b865ed]
xlabel('SNR (dB)'); ylabel('MSE / rad^2'); grid on; %[output:48b865ed]
legend('CSML', 'MUSIC', 'R-MUSIC', 'CRB'); %[output:48b865ed]
title(['DoA Est with ULA (Single Sat, Sinlge Source, ', num2str(numSnap), ' snaps)']); %[output:48b865ed]

